mapper.update:
UPDATE permission_templates
SET name = #{name}, description = #{description}, key_pattern = #{keyPattern}, updated_at = #{updatedAt}
WHERE id = #{id}

mapper.delete:
DELETE FROM perm_templates_users
WHERE template_id = #{templateId}
AND user_id = #{userId}
AND permission_reference = #{permission}

DELETE FROM perm_templates_groups
WHERE template_id = #{templateId}
AND permission_reference = #{permission}
AND


    group_id = #{groupId}


    group_id IS NULL

mapper.sql:
FROM users u
LEFT JOIN perm_templates_users ptu ON ptu.user_id=u.id
  AND ptu.template_id=#{templateId}
INNER JOIN organization_members om ON u.id=om.user_id
  AND om.organization_uuid=#{query.organizationUuid}

  u.active = ${_true}

    AND lower(u.name) like #{query.searchQueryToSqlLowercase} ESCAPE '/'


    and ptu.permission_reference is not null

      and ptu.permission_reference=#{query.permission}

FROM (SELECT g.id AS group_id, g.name AS name, ptg.permission_reference AS permission, ptg.template_id AS templateId FROM groups g LEFT JOIN perm_templates_groups ptg ON ptg.group_id=g.id AND ptg.template_id=#{templateId} where g.organization_uuid=#{query.organizationUuid,jdbcType=VARCHAR} UNION ALL SELECT 0 AS group_id, 'Anyone' AS name, ptg.permission_reference AS permission, ptg.template_id AS templateId FROM groups g LEFT JOIN perm_templates_groups ptg ON ptg.template_id=#{templateId}
g.organization_uuid=#{query.organizationUuid,jdbcType=VARCHAR}
AND ptg.group_id IS NULL
) groups
AND LOWER(groups.name) LIKE #{query.searchQueryToSqlLowercase} ESCAPE '/'
AND groups.permission IS NOT NULL AND groups.templateId=#{templateId}
AND groups.permission=#{query.permission}

mapper.select:
SELECT

FROM perm_templates_users ptu
INNER JOIN users u ON u.id = ptu.user_id AND u.active = ${_true}

  AND ptu.template_id = #{templateId}

    AND u.login IN 
      #{login}

SELECT sub.id, sub.templateId, sub.permission, sub.groupId, sub.groupName, sub.createdAt, sub.updatedAt FROM ( SELECT ptg.id, ptg.template_id as templateId, ptg.permission_reference AS permission, ptg.group_id AS groupId, g.name AS groupName, ptg.created_at as createdAt, ptg.updated_at as updatedAt FROM perm_templates_groups ptg INNER JOIN groups g ON g.id=ptg.group_id UNION ALL SELECT ptg.id, ptg.template_id as templateId, ptg.permission_reference AS permission, 0 AS groupId, 'Anyone' AS groupName, ptg.created_at as createdAt, ptg.updated_at as updatedAt FROM perm_templates_groups ptg WHERE ptg.group_id IS NULL ) sub
sub.templateId=#{templateId}
AND sub.groupName IN #{group}

-- from template users select ptu.permission_reference as permission_key from perm_templates_users ptu
and ptu.user_id=#{userId} and ptu.template_id=#{templateId}
UNION -- from template groups except anyone group select ptg.permission_reference as permission_key from perm_templates_groups ptg inner join groups_users gu on ptg.group_id = gu.group_id
and gu.user_id=#{userId} and ptg.template_id=#{templateId}
UNION -- from template characteristics select ptc.permission_key as permission_key from perm_tpl_characteristics ptc
and with_project_creator = ${_true} and ptc.template_id = #{templateId}
UNION
-- from anyone group select ptg.permission_reference as permission_key from perm_templates_groups ptg where ptg.template_id=#{templateId} and ptg.group_id IS NULL

SELECT ptu.template_id as templateId, ptu.permission_reference as permission, count(u.login) as count FROM users u INNER JOIN perm_templates_users ptu ON ptu.user_id=u.id AND ptu.template_id in
#{id}
AND u.active = ${_true}
GROUP BY ptu.template_id, ptu.permission_reference

SELECT count(1) as count, permission, templateId FROM (SELECT g.name as name, ptg.permission_reference as permission, ptg.template_id as templateId FROM groups g INNER JOIN perm_templates_groups ptg ON ptg.group_id=g.id UNION -- Add Anyone group permission SELECT #{anyoneGroup} as name, ptg.permission_reference as permission, ptg.template_id as templateId FROM perm_templates_groups ptg
AND ptg.group_id IS NULL
) groups
AND groups.templateId in
#{id}
GROUP BY groups.permission, groups.templateId

select count(1)
from perm_templates_groups ptg
where ptg.template_id = #{templateId}
and ptg.permission_reference = #{permission}
and

  ptg.group_id is null


  ptg.group_id = #{groupId}
